from maya.app.flux.imports import *
import maya.app.flux.core as fx
import maya.app.TTF.utils as tUtils

import maya.app.TTF.api
dm = lambda: maya.app.TTF.api.dataManager()

def getPixmapForCommand(command):
    iconName = dm().getImageForCommand(command)

    pixmap = fx.getPixmap(iconName)

    if pixmap is None:
        pixmap = fx.getPixmap('out_MASH_Utilities')

    return pixmap

def showDocsForCommand(cmd):
    if dm().getContext() == 'select': 
        return
    elif dm().getContext() == 'ttf':
        if cmd.helpUrl:
            tUtils.openWebUrl(cmd.helpUrl)
        else:
            tUtils.openWebSearch(cmd.label)
    else:
        lang = 'python' if dm().getContext() == 'python' else 'mel'
        tUtils.openWebDocs(cmd.rtc, lang)
