try:
    from inspect import getfullargspec as get_args
except ImportError:
    from inspect import getargspec as get_args

def abstractmethod(f):
    '''Decorator for an abstract method. Raise an exception if the function is not overridden by child class.'''
    def wrapper(*args, **kwargs):
        self = args[0]
        import inspect
        # getargspec was deprecated, pytlint reports the deprecation here.
        # pylint: disable=deprecated-method
        raise RuntimeError("Abstract function called from: " + self.__class__.__name__ +"."+ f.__name__ +"("+ str(get_args(f))+")")
    return wrapper
