"""
This module is imported during the startup of Maya in batch mode.
"""

import sys as local_sys
import platform as local_platform

if local_platform.system() == "Windows" and local_sys.version_info >= (3,8):
	import os as local_os
	with local_os.add_dll_directory( local_os.path.join(local_os.environ["MAYA_LOCATION"], "bin") ):
		import maya.app.startup.basic
	del local_os
else :
	import maya.app.startup.basic

del local_platform
del local_sys

# Run the user's userSetup.py if it exists
maya.app.startup.basic.executeUserSetup()

pass
