from maya.app.prefs.OptionVar import OptionVar, OPTION_VAR_TYPE_BOOL, OPTION_VAR_TYPE_FLOAT, OPTION_VAR_TYPE_STRING
from maya.common.utils import Singleton
from future.utils import with_metaclass

PREF_CATEGORY = r'Sound'

class SoundPreferenceVolume(with_metaclass(Singleton, OptionVar)):
    '''
    Class containing the information for the "sound volume" preference.
    '''
    ov_id = 'soundVolume' # optionVar name and unique preference ID

    def __init__(self):
        ''' Initialize the preference interface '''
        super( SoundPreferenceVolume, self ).__init__(self.ov_id, OPTION_VAR_TYPE_FLOAT, 1, PREF_CATEGORY)
        self.title = "Volume"
        self.info  = "Specify sound volume"


class SoundPreferenceVolumeBeforeMute(with_metaclass(Singleton, OptionVar)):
    '''
    Class containing the information for the "sound turned on" preference.
    '''
    ov_id = 'soundVolumeBeforeMute' # optionVar name and unique preference ID

    def __init__(self):
        ''' Initialize the preference interface '''
        super( SoundPreferenceVolumeBeforeMute, self ).__init__(self.ov_id, OPTION_VAR_TYPE_FLOAT, 1, PREF_CATEGORY)
        self.title = "Volume Before Mute"
        self.info  = "Specify sound volume before mute setup"


class SoundPreferencesWaveformDisplayType(with_metaclass(Singleton, OptionVar)):
    '''
    Class containing the information for the "audio waveform display" preference.
    '''
    ov_id = 'timeSliderSoundDisplay' # optionVar name and unique preference ID

    def __init__(self):
        ''' Initialize the preference interface '''
        super( SoundPreferencesWaveformDisplayType, self ).__init__(self.ov_id, OPTION_VAR_TYPE_STRING, "both", PREF_CATEGORY)
        self.title = "Waveform display"
        self.info  = "Specify how waveform should look like"
