'''
Maya Instruction on how to create a control for a color attribute

Read the Readme.md for further information
'''

import maya.cmds as cmds
from . import template


def replace ( controlName, plugName, attrName, changedCommand ):
    #    In case of multi, the label has to be updated to point a right index
    cmds.attrColorSliderGrp(controlName, e=True, label=attrName, attribute=plugName)
    template.attachCallback(plugName, controlName, changedCommand)




def create( plugName, attrName, changedCommand ):
    createdControl = cmds.attrColorSliderGrp(l=attrName, attribute=plugName)
    template.attachCallback(plugName, createdControl, changedCommand)
    return createdControl
