"""
General message utilities.
These methods are used as a way to standardize how errors, warnings and
inofrmationmessages will be presented.
"""

import maya.cmds

# ------------------------------------------------------------------------------
def showWarning(msg, noContext=True):
    '''Display a warning message to the user'''
    maya.cmds.warning(msg, noContext=noContext)

# ------------------------------------------------------------------------------
def showError(msg, noContext=True):
    '''Display an error message to the user'''
    maya.cmds.error(msg, noContext=noContext)
