import maya.internal.common.window.window_box as wnd

globalOptionBox = None

# ------------------------------------------------------------------------------
#
#  OptionBox
#
# ------------------------------------------------------------------------------

class OptionBox(wnd.WindowBox):

    def __init__(self):
        super(OptionBox, self).__init__()
        self.showApplyBtn = True
        self.showApplyAndCloseBtn = True

    def createWindowMenus(self):
        '''Creates the optionBox menus
        '''
        super(OptionBox, self).createWindowMenus()

        self.createEditMenu()
        self.createHelpMenu()

