# ------------------------------------------------------------------------------
def getOptionBox():
    '''Get an initialized singular global optionbox.
    This will create it, if necessary.
    '''
    global globalOptionBox

    if globalOptionBox is None:
        globalOptionBox = OptionBox()

    globalOptionBox.getOrCreateWindow()

    return globalOptionBox

# ------------------------------------------------------------------------------
def showOptionBox():
    '''Show the singular global optionbox.
    '''
    global globalOptionBox
    if globalOptionBox is None:
        return

    globalOptionBox.showWindow()

# ------------------------------------------------------------------------------
def hideOptionBox(now=False):
    '''Hide the singular global optionbox.
    '''
    global globalOptionBox
    if globalOptionBox is None:
        return

    globalOptionBox.hideWindow(now)
