import maya.internal.common.window.window_box as wnd

globalOptionVarBox = None

# ------------------------------------------------------------------------------
#
#  OptionVarBox
#
# ------------------------------------------------------------------------------

'''
    This is a version of a global option box which will allow for editing
    of certain option vars without a particular action to apply them to
    immediately. That is why the apply and applyAndClose button are hidden.
'''
class OptionVarBox(wnd.WindowBox):

    def __init__(self):
        super(OptionVarBox, self).__init__()
        self.showApplyBtn = False
        self.showApplyAndCloseBtn = False

    def createWindowMenus(self):
        '''Creates the optionVarBox menus
        '''
        super(OptionVarBox, self).createWindowMenus()

        self.createEditMenu()
        self.createHelpMenu()

# ------------------------------------------------------------------------------
#
# Global calls
#
# ------------------------------------------------------------------------------

def getOptionVarBox():
    '''Get an initialized singular global optionbox.
    This will create it, if necessary.
    '''
    global globalOptionVarBox

    if globalOptionVarBox is None:
        globalOptionVarBox = OptionVarBox()

    globalOptionVarBox.getOrCreateWindow()

    return globalOptionVarBox
