"""
bend -
    Module containing functions to create setup with the bend deformer
"""
import maya
maya.utils.loadStringResourcesForModule(__name__)


import maya.cmds

import maya.internal.common.cmd.deformer as cmddeformer
import maya.internal.common.utils.ui as ui_utils

# ------------------------------------------------------------------------------
# Command
# ------------------------------------------------------------------------------
class Command(cmddeformer.Command):

    def __init__(self):
        super(Command, self).__init__()

        self.commandName = 'Bend'
        self.commandHelpTag	= 'Bend'
        self.commandDescription = maya.stringTable['y_maya_internal_nodes_bend_cmd_create.kBendCommandDescription' ]
        self.commandTitle = maya.stringTable['y_maya_internal_nodes_bend_cmd_create.kBendCommandTitle' ]
        self.optionVarCategory = 'Deform.Bend'
        self.optionVarPrefix = 'Bend'
        self.optionVarDefaults.update( {
            'lowBound': -1.0,
            'highBound': 1.0,
            'curvature': 0.0 } )

    @classmethod
    def command(cls, **kwargs):
        '''Bend deformer
        '''
        returnedNodes = list()

        result = cmddeformer.Command.createNonLinearDeformer(deformerType='bend', **kwargs)

        if result is not None:
            deformerNode = result[0]

            settableAttrs = [ 'lowBound',
                              'highBound',
                              'curvature']

            cls.setNodeAttributes(deformerNode, kwargs, settableAttrs)

            returnedNodes.append(deformerNode)

        return cls.finalizeCommand(returnedNodes)

    def addBasicDeformerDialogWidgets(self):

        widgetDict = {} # {optionVarDictKey, (widgetClass, widget)}

        with ui_utils.AttributeLayoutManager():
            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_bend_cmd_create.kBendLabelLowBound' ], -10, 0.0, llim=False)
            widgetDict['lowBound'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_bend_cmd_create.kBendLabelHighBound' ], 0.0, 10.0, ulim=False)
            widgetDict['highBound'] = (maya.cmds.floatSliderGrp, widget)

            # set create options unit consistent with preference
            currUnit = maya.cmds.currentUnit(query=True,fullName=True, angle=True)
            if  currUnit == 'degree':
                minCurv = -180.0
                maxCurv =  180.0
            else:
                minCurv = -3.14159265
                maxCurv =  3.14159265

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_bend_cmd_create.kBendLabelCurvature' ], minCurv, maxCurv)
            widgetDict['curvature'] = (maya.cmds.floatSliderGrp, widget)

        return widgetDict

