import maya
maya.utils.loadStringResourcesForModule(__name__)

import maya.internal.common.ae.template as aetemplate


class AETemplate(aetemplate.Template):
    def buildUI(self, nodename):
        with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_blendmatrix_ae_template.kLabelBlendMatrix' ]):
            self.addControls([
                'enable',
                'envelope'])
            self.suppress('outputMatrix)')

            with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_blendmatrix_ae_template.kLabelBlendInputMatrix' ], collapse=True):
                self.addControls(['inputMatrix'])

            with aetemplate.Layout(self,maya.stringTable['y_maya_internal_nodes_blendmatrix_ae_template.kLabelBlendTargetMatrix' ], collapse=True):
                self.addControls(['target'])

        self.callTemplate('dependNode')
