import maya
maya.utils.loadStringResourcesForModule(__name__)

import maya.internal.common.ae.template as aetemplate
import maya.internal.nodes.weightgeometryfilter.ae_template as ae
import maya.cmds as cmds

class AETemplate(ae.AETemplate):
    def buildUI(self, nodeName):
        with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_cluster_ae_template.kClusterAttributes' ], collapse=True):
            self.addControl("relative")
            self.addControls([
                "angleInterpolation",
            ])

        self.suppress("clusterXforms")
        self.suppress("matrix")
        self.suppress("bindPreMatrix")
        self.suppress("bindState")

        super(AETemplate, self).buildUI(nodeName)






