"""
cluster -
    Module containing functions to create setup with the cluster deformer
"""
import maya
maya.utils.loadStringResourcesForModule(__name__)


import maya.cmds

import maya.internal.common.cmd.deformer as cmddeformer
import maya.internal.common.utils.ui as ui_utils

# ------------------------------------------------------------------------------
# Command
# ------------------------------------------------------------------------------
class Command(cmddeformer.Command):

    def __init__(self):
        super(Command, self).__init__()

        self.commandName = 'Cluster'
        self.commandHelpTag	= 'Cluster'
        self.commandDescription = maya.stringTable['y_maya_internal_nodes_cluster_cmd_create.kClusterCommandDescription' ]
        self.commandTitle = maya.stringTable['y_maya_internal_nodes_cluster_cmd_create.kClusterCommandTitle' ]
        self.optionVarCategory = 'Deform.Cluster'
        self.optionVarPrefix = 'Cluster'
        self.optionVarDefaults.update( {
            'relative': False,
            'envelope': 1.0 } )

    @classmethod
    def command(cls, **kwargs):
        '''Cluster deformer
        '''
        returnedNodes = list()

        kw = cmddeformer.Command.getDeformerCommandArgs(**kwargs)
        try:
            result = maya.cmds.cluster(**kw)
        except Exception:
            result = None

        if result is not None:
            deformerNode = result[0]

            settableAttrs = [ 'relative', 'envelope' ]

            cls.setNodeAttributes(deformerNode, kwargs, settableAttrs)

            returnedNodes.append(deformerNode)

        return cls.finalizeCommand(returnedNodes)

    def addBasicDeformerDialogWidgets(self):

        widgetDict = {} # {optionVarDictKey, (widgetClass, widget)}

        with ui_utils.AttributeLayoutManager():
            widget = ui_utils.checkBoxGrp(maya.stringTable['y_maya_internal_nodes_cluster_cmd_create.kClusterLabelRelative' ])
            widgetDict['relative'] = (maya.cmds.checkBoxGrp, widget)

            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_cluster_cmd_create.kClusterLabelEnvelope' ], 0.0, 1.0)
            widgetDict['envelope'] = (maya.cmds.floatSliderGrp, widget)

        return widgetDict

