from maya.internal.common.node_interface import NodeInterface as BaseNodeInterface

class NodeInterface(BaseNodeInterface):
    _mayaNodeType = 'componentMatch'

    def __init__(self, nodeName):
        super(NodeInterface, self).__init__(nodeName)

    # --------------------------------------------------------------------------
    # Plug names
    # --------------------------------------------------------------------------
    def inputGeometryPlugName(self):
        return '{}.inputGeometry'.format(self.getEditNode())

    def targetGeometryPlugName(self):
        return '{}.targetGeometry'.format(self.getEditNode())
