# File: ae_template.py
import maya
maya.utils.loadStringResourcesForModule(__name__)


import maya.internal.common.ae.template as aetemplate

AttrList = (
    'controllerObject',
    'visibilityMode',
    'cycleWalkSibling',
    'parent',
    'children',
    'parentprepopulate',
    'prepopulate'
)

#-------------------------------------------------------------------------------

class AETemplate(aetemplate.Template):
    def buildUI(self, nodename):
        layoutName = maya.stringTable['y_maya_internal_nodes_controller_ae_template.kControllerAttributes' ]
        with aetemplate.Layout(self, layoutName):
            self.addControls(AttrList)

        self.callTemplate('dependNode')
