import maya
maya.utils.loadStringResourcesForModule(__name__)

import maya.internal.common.ae.template as aetemplate


class AETemplate(aetemplate.Template):
    def buildUI(self, nodename ):
        with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_decomposematrix_ae_template.kLabelDecomposeMatrix' ]):
            with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_decomposematrix_ae_template.kInputProperties' ]):
                self.addControls(['inputMatrix'])

            with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_decomposematrix_ae_template.kOutputProperties' ]):
                 self.addControls([
                    'outputTranslate',
                    'outputRotate',
                    'outputScale',
                    'outputShear',
                    'outputQuat' ])

        self.callTemplate('dependNode')
