import maya
maya.utils.loadStringResourcesForModule(__name__)

import maya.cmds as cmds
import maya.internal.nodes.weightgeometryfilter.ae_template as wgfae
from maya.internal.common.ae.template import Layout

class AETemplate(wgfae.AETemplate):
    def buildUI(self, nodeName):
        with Layout(self, maya.stringTable['y_maya_internal_nodes_deltamush_ae_template.kDeltaMushAttributes' ], collapse=False):
            self.addControls([
                "smoothingIterations",
                "smoothingStep",
                "pinBorderVertices",
                "displacement",
                "scale",
                "inwardConstraint",
                "outwardConstraint",
                "distanceWeight"
            ])

        self.suppress("smoothingAlgorithm")
        self.suppress("bindData")


        super(AETemplate, self).buildUI(nodeName)
