"""
flare -
    Module containing functions to create setup with the flare deformer
"""
import maya
maya.utils.loadStringResourcesForModule(__name__)


import maya.cmds

import maya.internal.common.cmd.deformer as cmddeformer
import maya.internal.common.utils.ui as ui_utils

# ------------------------------------------------------------------------------
# Command
# ------------------------------------------------------------------------------
class Command(cmddeformer.Command):

    def __init__(self):
        super(Command, self).__init__()

        self.commandName = 'Flare'
        self.commandHelpTag	= 'Flare'
        self.commandDescription = maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareCommandDescription' ]
        self.commandTitle = maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareCommandTitle' ]
        self.optionVarCategory = 'Deform.Flare'
        self.optionVarPrefix = 'Flare'
        self.optionVarDefaults.update( {
            'lowBound': -1.0,
            'highBound': 1.0,
            'startFlareX': 1.0,
            'startFlareZ': 1.0,
            'endFlareX': 1.0,
            'endFlareZ': 1.0,
            'curve': 0.0 } )

    @classmethod
    def command(cls, **kwargs):
        '''Flare deformer
        '''
        returnedNodes = list()

        result = cmddeformer.Command.createNonLinearDeformer(deformerType='flare', **kwargs)

        if result is not None:
            deformerNode = result[0]
            settableAttrs = [ 'lowBound',
                              'highBound',
                              'startFlareX',
                              'startFlareZ',
                              'endFlareX',
                              'endFlareZ',
                              'curve' ]

            cls.setNodeAttributes(deformerNode, kwargs, settableAttrs)

            returnedNodes.append(deformerNode)

        return cls.finalizeCommand(returnedNodes)

    def addBasicDeformerDialogWidgets(self):

        widgetDict = {} # {optionVarDictKey, (widgetClass, widget)}

        with ui_utils.AttributeLayoutManager():
            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelLowBound' ], -10, 0.0, llim=False)
            widgetDict['lowBound'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelHighBound' ], 0.0, 10.0, ulim=False)
            widgetDict['highBound'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelStartFlareX' ], 0.0, 10.0)
            widgetDict['startFlareX'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelStartFlareZ' ], 0.0, 10.0)
            widgetDict['startFlareZ'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelEndFlareX' ], 0.0, 10.0)
            widgetDict['endFlareX'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelEndFlareZ' ], 0.0, 10.0)
            widgetDict['endFlareZ'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_flare_cmd_create.kFlareLabelCurve' ], -10.0, 10.0)
            widgetDict['curve'] = (maya.cmds.floatSliderGrp, widget)

        return widgetDict

