import maya
maya.utils.loadStringResourcesForModule(__name__)

from maya.internal.nodes.ffd.ae_template import AETemplate as AETemplateBase
from maya.internal.common.ae.template import Layout


class AETemplate(AETemplateBase):
    def buildUI(self, nodeName):
        layoutName = maya.stringTable[\
            'y_maya_internal_nodes_jointffd_ae_template.kJointFreeformDeformationAttributes'\
            ]
            
        with Layout(self, layoutName):
            self.addControl("useComponentCache")
            self.addSeparator()
            self.buildCommon()
        
        self.suppress('groupIdUpperBindSkin')
        self.suppress('groupIdLowerBindSkin')
        # build remaining parts from grandparent (geometryFilter)
        super(AETemplateBase, self).buildUI(nodeName)
