import maya
maya.utils.loadStringResourcesForModule(__name__)

import maya.internal.common.ae.template as aetemplate


class AETemplate(aetemplate.Template):
    def buildUI(self, nodename):
        with aetemplate.Layout(self, maya.stringTable['y_maya_internal_nodes_pickmatrix_ae_template.kLabelPickMatrix' ]):
            self.addControls([
                'inputMatrix',
                'useTranslate',
                'useRotate',
                'useScale',
                'useShear'])
            self.suppress('outputMatrix)')

        self.callTemplate('dependNode')
