"""
twist -
    Module containing functions to create setup with the twist deformer
"""
import maya
maya.utils.loadStringResourcesForModule(__name__)


import maya.cmds

import maya.internal.common.cmd.deformer as cmddeformer
import maya.internal.common.utils.ui as ui_utils

# ------------------------------------------------------------------------------
# Command
# ------------------------------------------------------------------------------
class Command(cmddeformer.Command):

    def __init__(self):
        super(Command, self).__init__()

        self.commandName = 'Twist'
        self.commandHelpTag	= 'Twist'
        self.commandDescription = maya.stringTable['y_maya_internal_nodes_twist_cmd_create.kTwistCommandDescription' ]
        self.commandTitle = maya.stringTable['y_maya_internal_nodes_twist_cmd_create.kTwistCommandTitle' ]
        self.optionVarCategory = 'Deform.Twist'
        self.optionVarPrefix = 'Twist'
        self.optionVarDefaults.update( {
            'lowBound': -1.0,
            'highBound': 1.0,
            'startAngle': 0.0,
            'endAngle': 0.0 } )

    @classmethod
    def command(cls, **kwargs):
        '''Twist deformer
        '''
        returnedNodes = list()

        result = cmddeformer.Command.createNonLinearDeformer(deformerType='twist', **kwargs)

        if result is not None:
            deformerNode = result[0]

            settableAttrs = [ 'lowBound',
                              'highBound',
                              'startAngle',
                              'endAngle' ]

            cls.setNodeAttributes(deformerNode, kwargs, settableAttrs)

            returnedNodes.append(deformerNode)

        return cls.finalizeCommand(returnedNodes)

    def addBasicDeformerDialogWidgets(self):

        widgetDict = {} # {optionVarDictKey, (widgetClass, widget)}

        with ui_utils.AttributeLayoutManager():
            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_twist_cmd_create.kTwistLabelLowBound' ], -10, 0.0, llim=False)
            widgetDict['lowBound'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_twist_cmd_create.kTwistLabelHighBound' ], 0.0, 10.0, ulim=False)
            widgetDict['highBound'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_twist_cmd_create.kTwistLabelStartAngle' ], -10.0, 10.0)
            widgetDict['startAngle'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_twist_cmd_create.kTwistLabelEndAngle' ], -10.0, 10.0)
            widgetDict['endAngle'] = (maya.cmds.floatSliderGrp, widget)

        return widgetDict

