import maya
maya.utils.loadStringResourcesForModule(__name__)

import maya.cmds
import maya.internal.common.ae.template as aetemplate
import maya.internal.common.utils.geometry as geo_utils
import maya.internal.common.utils.ui as ui_utils
import maya.internal.common.qt.buttons as qt_buttons
import maya.internal.nodes.common.pin.node_interface as pin_ifc
import maya.internal.nodes.uvpin.node_interface as ifc
import maya.internal.nodes.uvpin.cmd_optionvar as opv

# --------------------------------------------------------------------------------------------------------------
class ManagePinsWidget(qt_buttons.ManageMenuButtonWidget):

    def __init__(self, uiParent=None):
        super(ManagePinsWidget, self).__init__(
            label = maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kManagePins' ],
            annotation = maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kManagePinsAnnot' ],
            uiParent=uiParent)

        self.selectGeomAct = self.addMenuAction(maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kSelectGeom' ], annotation=maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kSelectGeomAnnot' ])
        self.replaceGeomAct = self.addMenuAction(maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kReplaceGeom' ], annotation=maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kReplaceGeomAnnot' ])
        self.addMenuSeparator()
        self.createPinXformAct = self.addMenuAction(maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kCreatePinXform' ])
        self.createPinCompAct = self.addMenuAction(maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kCreatePinComp' ])
        self.addMenuSeparator()
        self.editDefaultsAct = self.addMenuAction(maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kEditDefaultsItem' ], ':/menu_options.png')

    @ui_utils.undoChunk('replace geometry on proximityPin')
    def _replaceGeometry(self, editor):
         editor.setGeometry(self.replaceGeom)

    @ui_utils.undoChunk('create pins from transforms')
    def _createPinsFromTransforms(self, editor):
        optionVars = opv.Command().getOptionVars()
        editor.addInputsFromClosestPointToTransforms(transforms=self.unusedTransforms, **optionVars)

    @ui_utils.undoChunk('create pins from components')
    def _createPinsFromComponents(self, editor):
        optionVars = opv.Command().getOptionVars()
        editor.addInputsFromSelectedComponents(**optionVars)

    def enableMenuItems(self):
        editor = ifc.NodeInterface(self.nodeName)

        self.geom = None
        self.replaceGeom = None
        self.unusedTransforms = list()
        self.componentSelected = False

        if editor.isValid():
            self.geom = editor.getGeometry()
            selectedShapes = geo_utils.getSelectedShapes(geomType=editor.driverGeomTypeFilter())
            if self.geom in selectedShapes:
                self.componentSelected = geo_utils.hasComponentsSelected(self.geom)
            else:
                self.replaceGeom = selectedShapes[0] if len(selectedShapes) == 1 else None
                selected = set(maya.cmds.ls(sl=True, type='transform'))

                conOutputs = set(editor.getConnectedMatrixOutputs())
                self.unusedTransforms = list(selected.difference(conOutputs))

        self.selectGeomAct.setEnabled(self.geom is not None)
        self.replaceGeomAct.setEnabled(self.replaceGeom is not None)
        self.createPinXformAct.setEnabled(len(self.unusedTransforms) > 0)
        self.createPinCompAct.setEnabled(self.componentSelected)

    def actionTriggered(self, action):
        if action == self.editDefaultsAct:
            # popup the dialog to edit the defaults
            opv.Command().createDialog()
            return

        editor = ifc.NodeInterface(self.nodeName)
        if not editor.isValid():
            return

        if action == self.selectGeomAct:
            maya.cmds.select(self.geom, replace=True)
        elif action == self.replaceGeomAct:
             self._replaceGeometry(editor)
        elif action == self.createPinCompAct:
            self._createPinsFromComponents(editor)
        elif action == self.createPinXformAct:
            self._createPinsFromTransforms(editor)

# --------------------------------------------------------------------------------------------------------------
class AETemplate(aetemplate.Template):

    prevNormalAxis = None
    prevTangentAxis = None

    def buildUI(self, nodeName):
        AETemplate.prevNormalAxis = maya.cmds.getAttr(u"{0}.normalAxis".format(nodeName))
        AETemplate.prevTangentAxis = maya.cmds.getAttr(u"{0}.tangentAxis".format(nodeName))

        maya.cmds.editorTemplate(beginLayout=maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kLabelPinAttr' ], collapse=False)
        self.defineQtCustom(ManagePinsWidget, 'coordinate')

        self.addControl('normalAxis', callback=self._onUpdateNormalAxis)
        self.addControl('tangentAxis', callback=self._onUpdateTangentAxis)
        self.addControl('uvSetName')
        self.addControl('normalizedIsoParms', label=maya.stringTable['y_maya_internal_nodes_uvpin_ae_template.kLabelUVPinNormIsoparms' ])
        self.addControl('normalOverride')
        self.addControl('coordinate')

        self.suppress('deformedGeometry')
        self.suppress('originalGeometry')
        self.suppress('railCurve')
        maya.cmds.editorTemplate(endLayout=True)

        self.callTemplate('dependNode')

    @staticmethod
    def _onUpdateNormalAxis(nodeName):
        AETemplate.prevNormalAxis = pin_ifc.resolveConflictingAxisAttributeEditor(nodeName, AETemplate.prevNormalAxis, True)

    @staticmethod
    def _onUpdateTangentAxis(nodeName):
        AETemplate.prevTangentAxis = pin_ifc.resolveConflictingAxisAttributeEditor(nodeName, AETemplate.prevTangentAxis, False)

