"""
wave -
    Module containing functions to create setup with the wave deformer
"""
import maya
maya.utils.loadStringResourcesForModule(__name__)


import maya.cmds

import maya.internal.common.cmd.deformer as cmddeformer
import maya.internal.common.utils.ui as ui_utils

# ------------------------------------------------------------------------------
# Command
# ------------------------------------------------------------------------------
class Command(cmddeformer.Command):

    def __init__(self):
        super(Command, self).__init__()

        self.commandName = 'Wave'
        self.commandHelpTag	= 'Wave'
        self.commandDescription = maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveCommandDescription' ]
        self.commandTitle = maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveCommandTitle' ]
        self.optionVarCategory = 'Deform.Wave'
        self.optionVarPrefix = 'Wave'
        self.optionVarDefaults.update( {
            'minRadius': 0.0,
            'maxRadius': 1.0,
            'amplitude': 0.0,
            'wavelength': 1.0,
            'dropoff': 0.0,
            'offset': 0.0 } )

    @classmethod
    def command(cls, **kwargs):
        '''Wave deformer
        '''
        returnedNodes = list()

        result = cmddeformer.Command.createNonLinearDeformer(deformerType='wave', **kwargs)

        if result is not None:
            deformerNode = result[0]

            settableAttrs = [ 'maxRadius',
                              'minRadius',
                              'amplitude',
                              'wavelength',
                              'dropoff',
                              'offset' ]

            cls.setNodeAttributes(deformerNode, kwargs, settableAttrs)

            returnedNodes.append(deformerNode)

        return cls.finalizeCommand(returnedNodes)

    def addBasicDeformerDialogWidgets(self):

        widgetDict = {} # {optionVarDictKey, (widgetClass, widget)}

        with ui_utils.AttributeLayoutManager():
            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveLabelMinRadius' ], 0.0, 10.0, ulim=False)
            widgetDict['minRadius'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveLabelMaxRadius' ], 0.0, 10.0, ulim=False)
            widgetDict['maxRadius'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveLabelAmplitude' ], -5.0, 5.0)
            widgetDict['amplitude'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveLabelWaveLength' ], -0.1, 10.0)
            widgetDict['wavelength'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrp(maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveLabelDropoff' ], -1.0, 1.0, step=0.1)
            widgetDict['dropoff'] = (maya.cmds.floatSliderGrp, widget)

            widget = ui_utils.floatSliderGrpNoLimit(maya.stringTable['y_maya_internal_nodes_wave_cmd_create.kWaveLabelOffset' ], -10.0, 10.0)
            widgetDict['offset'] = (maya.cmds.floatSliderGrp, widget)

        return widgetDict

