# File: ae_template.py
import maya
maya.utils.loadStringResourcesForModule(__name__)

from maya.internal.common.ae.template import Layout
from maya.internal.nodes.geometryfilter.ae_template import AETemplate as AETemplateBase
from maya.internal.nodes.wire.ae_custom_attr import AEwireAttrControl
from maya.internal.nodes.wire.ae_custom_locator import AEwireLocatorControl
import maya.cmds as cmds

ParametersAttrs = (
    "rotation",
    "crossingEffect",
    "localInfluence",
    "tension",
    "freezeGeometry",
    "bindToOriginalGeometry")


class AETemplate(AETemplateBase):
    def buildUI(self, nodeName):
        with Layout(self, maya.stringTable['y_maya_internal_nodes_wire_ae_template.kParameters' ]):
            self.addControls(ParametersAttrs)

        with Layout(self, maya.stringTable['y_maya_internal_nodes_wire_ae_template.kScale' ], collapse=True):
            self.defineCustom(AEwireAttrControl(), "scale")

        with Layout(self, maya.stringTable['y_maya_internal_nodes_wire_ae_template.kDropoffDistance' ], collapse=True):
            self.defineCustom(AEwireAttrControl(), "dropoffDistance")

        with Layout(self, maya.stringTable['y_maya_internal_nodes_wire_ae_template.kLocators' ], collapse=True):
            self.defineCustom(AEwireLocatorControl(), "locators")

        self.suppress("wireLocatorPercentage")
        self.suppress("wireLocatorParameter")
        self.suppress("wireLocatorEnvelope")
        self.suppress("wireLocatorTwist")
        self.suppress("deformedWire")
        self.suppress("baseWire")
        self.suppress("holder")
        self.suppress("weightList")
        self.suppress("weights")

        super().buildUI(nodeName)
