# File: ui_template.py
import maya.cmds as cmds

class UITemplate:
    def __enter__(self):
        cmds.setUITemplate("attributeEditorTemplate", pst=True)
        return self

    def __exit__(self, mytype, value, tb):
        cmds.setUITemplate(ppt=True)
