import maya.OpenMayaUI as mui
import maya.cmds as cmds
from shiboken2 import wrapInstance

from PySide2.QtWidgets import QWidget

def insertLayerManagerInLayout(layout):
    ptr = mui.MQtUtil.findLayout(layout)
    if ptr is not None:
        mayaWidget = wrapInstance(int(ptr), QWidget)
        mayaLayout = mayaWidget.layout()

        layerManager = cmds.bluePencilUtil(query=True, layerManager=True)
        widgetPtr = mui.MQtUtil.findControl(layerManager)

        if widgetPtr is not None:
            widget = wrapInstance(int(widgetPtr), QWidget)
            widget.setParent(mayaWidget)
            mayaLayout.addWidget(widget)

def DPI(val):
    return mui.MQtUtil.dpiScale(val)
