"""
    Definition of common enumerators
"""


class RelTo(object):
    """ Enum used to set point of reference to the parent window a position is """
    Screen = 0

    TopLeft = 5
    TopRight = 6
    BottomLeft = 7
    BottomRight = 8

    Top = 10
    Bottom = 11
    Left = 12
    Right = 13

    Center = 15

    AttachToAllSides = 20  # make the overlay be as large as parent window


class OverlayStyle(object):
    """ drawing style for an overlay """
    Custom = 0
    ToolTip = 1
    SpeechBubble = 2
    Darkening = 3  # darkens the entire screen except for the rectangles set in setUndarkenRects


class Direction(object):
    Left = 1
    Top = 2
    Right = 3
    Bottom = 4
