"""
Autodesk HIG css for Qt
"""

from PySide2.QtCore import QRegExp
import copy
from .utils import DPIScale


def getHIGStyleSheet():
    css = copy.copy(HIG_CSS)
    regex = QRegExp("([0-9]+)px")
    index = regex.indexIn(css)
    prevIndex = 0
    newcss = ""
    while index != -1:
        digits = regex.cap(1)
        digitLen = len(digits)

        pixelValue = str(int(DPIScale(int(digits))))

        newcss += css[prevIndex:index] + pixelValue + "px"

        index += len(regex.cap(0))
        prevIndex = index
        index = regex.indexIn(css, index)

    newcss += css[prevIndex:]
    return newcss


