"""
    Tools to create and manage windows that overlay the main application
"""

# Qt
from PySide2.QtCore import QSize

# internal
from .managerImpl import OverlayManagerImpl
from .enums import RelTo, OverlayStyle
from .overlayDef import OverlayDef
from .overlay import Overlay


class OverlayManager:
    """ class to create and manage overlay windows """

    def __init__(self, mainApplicationWindow):
        self._impl = OverlayManagerImpl(mainApplicationWindow)

    def createOverlay(self, overlayDef):
        # type:  (OverlayDef) -> Overlay
        """ create an overlay, initially hidden """
        return self._impl.createOverlay(overlayDef)

    def createDarkeningOverlay(self):
        """ create an overlay that darkens the entier application"""
        return self._impl.createDarkeningOverlay()

    def deleteOverlay(self, overlay):
        # type:  (OverlayDef) -> None
        return self._impl.deleteOverlay(overlay)

    def deleteAll(self):
        """ close all overlays """
        return self._impl.deleteAll()

    def showAll(self, show=True):
        """ show or hide all the overlays """
        self._impl.showAll(show)

    def getMainWindow(self):
        """ returns the main application window """
        return self._impl.getMainWindow()
