import maya.OpenMayaUI as omui

# internal
from . import OverlayManager
from . import demo
from . import maya

from typing import Dict


class MayaDemoState(demo.DemoState):
    def __init__(self, manager):
        # type: (OverlayManager) -> None
        super(MayaDemoState, self).__init__(manager)
        # self.stages.append(self.highlightPanels)
        # self._demoState = 1
        controls = maya.findAlWorkspaceControls()
        self.rectWidget.allrects = dict()
        for key in controls:
            if controls[key].visible:
                self.rectWidget.allrects[key] = controls[key].rect


def demoOverlays():
    """ runs of demo of overlays in Maya """
    manager = maya.overlayManager()
    demo.demoProgressDialog(manager)
    demo.demoTourDialog(manager)
    mayaDemo = MayaDemoState(manager)
    mayaDemo.startDemo()
