""" class used to define the parameters for creating an overlay """

# Qt
from PySide2.QtCore import QRect, QSize
from PySide2.QtGui import QColor

# internal
from .enums import RelTo, OverlayStyle

from typing import List


class OverlayRect:
    """ defines one rectangle to be highlighted on a "darkening" overlay """

    def __init__(self, rect=QRect(), attachment=RelTo.Screen, offset=QSize(0, 0)):
        # type: (QRect, int, QSize) -> None
        self.rect = rect
        self.attachment = attachment
        self.offset = offset


OverlayRectList = List[OverlayRect]


class OverlayDef:
    """ class used to define the parameters for creating an overlay """

    def __init__(self, attachment=RelTo.Screen, offset=QSize(0, 0), style=OverlayStyle.ToolTip):
        # type : (int, QSize, int) -> None
        self.attachment = attachment
        self.offset = offset
        self.style = style

        self.bgColor = QColor(255, 237, 34, 192)

        # Mouse Enter/Leave alpha is set automatically to true when button has a push callback
        self.mouseEnterAlphaEnabled = False
        self.mouseInsideAlpha = 0.9
        self.mouseOutsideAlpha = 0.6

    def getDefaultAlpha(self):
        if self.mouseEnterAlphaEnabled:
            return self.mouseOutsideAlpha
        else:
            return self.bgColor.alphaF()
