""" Loader for Ufe python bindings """
from .PyUfe import *

typedAttributeDict = {
    Attribute.kBool: getAttributeBool, 
    Attribute.kInt: getAttributeInt, 
    Attribute.kFloat: getAttributeFloat, 
    Attribute.kDouble: getAttributeDouble, 
    Attribute.kString: getAttributeString, 
    Attribute.kColorFloat3: getAttributeColorFloat3, 
    Attribute.kEnumString: getAttributeEnumString, 
    Attribute.kInt3: getAttributeInt3, 
    Attribute.kFloat3: getAttributeFloat3, 
    Attribute.kDouble3: getAttributeDouble3, 
    Attribute.kGeneric: getAttributeGeneric
}

def typedAttributeFactory(attrs, attrName):
    attrType = attrs.attributeType(attrName)
    return typedAttributeDict[attrType](attrs, attrName)

# Create an 'attribute' method on the 'Attributes' class which will create and
# return a typed attribute.
Attributes.attribute = typedAttributeFactory
