float floatComposite(float floatA, float floatB, int operation, float factor) 
{
	float resultFloat = 0.0f;

	if (operation==0)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
			resultFloat = floatA + (floatB * factor);
		}
	}
	else if (operation==1)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
			resultFloat = floatA + ((floatB - 1) * factor);
		}
	}
	else if (operation==2)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else if ( factor >= 1.0f )
		{
			resultFloat = floatB;
		}
		else
		{
			resultFloat = lerp(floatA, floatB, factor);
		}
	}
	else if (operation==3)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
			resultFloat = floatA * (floatB * factor + (1 - factor));
		}
	}
	else if (operation==4)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
	        resultFloat = 1 - ((1 - floatA) * (1 - floatB * factor));
		}
	}
	else if (operation==5)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
			resultFloat = floatB > 0.5 ? (2 * floatA * floatB * factor ) + floatA * (1 - factor): 1 - ((1 - floatA) * (1 - floatB * factor)) * (2 - (1 - factor));
		}
	}
	else if (operation==6)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
	        resultFloat = abs(floatA - (floatB * factor));
    	}
	}
	else if (operation==7)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
	        resultFloat = clamp(lerp(floatA, floatA / max(1 - floatB, 0.00001), factor), floatA, floatB);
	    }
	}
	else if (operation==8)
	{
		if ( factor <= 0.0f )
		{
			resultFloat = floatA;
		}
		else
		{
	        resultFloat = clamp(lerp(floatA, 1 - (floatA / max(1 - floatB, 0.00001)), factor), floatA, floatB);
	    }
	}

	return resultFloat;
}


