bool floatLogic(float floatA, float floatB, int operation) 
{
	if (operation==0)
	{
		return (floatA == floatB);
	}
	else if (operation==1)
	{
		return (floatA != floatB);
	}
	else if (operation==2)
	{
		return (floatA < floatB);
	}
	else if (operation==3)
	{
		return (floatA > floatB);
	}
	else if (operation==4)
	{
		return (floatA <= floatB);
	}
	else if (operation==5)
	{
		return (floatA >= floatB);
	}
	else
	{
		return false;
	}
}


