float floatMath(float floatA, float floatB, int operation)
{
	float resultFloat = floatA;

	if (operation==0)
	{
		resultFloat += floatB ;
	}
	else if (operation==1)
	{
		resultFloat -= floatB ;
	}
	else if (operation==2)
	{
		resultFloat *= floatB ;
	}
	else if (operation==3)
	{
		resultFloat = floatB != 0.0 ? (floatA/floatB) : 0.0;
	}
	else if (operation==4)
	{
		if ( floatA > floatB )
		{
			resultFloat = floatB;
		}
	}
	else if (operation==5)
	{
		if ( floatA < floatB )
		{
			resultFloat = floatB;
		}
	}
	else if (operation==6)
	{
		resultFloat = pow( floatA, floatB );
	}

	return resultFloat;
}


