float3 premultiply(float3 inColor, float inAlpha)
{
	float3 resultColor;

	resultColor[0] = inColor[0] * inAlpha;
	resultColor[1] = inColor[1] * inAlpha;
	resultColor[2] = inColor[2] * inAlpha;

	return resultColor;
}


