float3 unpremultiply(float3 inColor, float inAlpha)
{
	float3 resultColor;
	float invAlpha = 1.0f / inAlpha;

	resultColor[0] = inColor[0] * invAlpha;
	resultColor[1] = inColor[1] * invAlpha;
	resultColor[2] = inColor[2] * invAlpha;

	return resultColor;
}


float3 unpremultiply(float3 inColor, float inAlpha)
{
	float3 resultColor;
	float invAlpha = 1.0f / inAlpha;

	resultColor[0] = inColor[0] * invAlpha;
	resultColor[1] = inColor[1] * invAlpha;
	resultColor[2] = inColor[2] * invAlpha;

	return resultColor;
}


vec3 unpremultiply(vec3 inColor, float inAlpha)
{
	vec3 resultColor;
	float invAlpha = (1.0f / inAlpha);

	resultColor[0] = inColor[0] * invAlpha;
	resultColor[1] = inColor[1] * invAlpha;
	resultColor[2] = inColor[2] * invAlpha;

	return resultColor;
}
